function animateSVG() {
  const flower = animatedSVG.value.querySelector("#flower"); // Reemplaza con el ID correcto de la flor
  const petals = animatedSVG.value.querySelectorAll(".petal"); // Reemplaza con la clase correcta de los pÃ©talos

  flower.style.transformOrigin = "center center";
  flower.style.transform = "scale(1.1)";
  flower.style.transition = "transform 1s ease-in-out";

  petals.forEach((petal, index) => {
    petal.style.transformOrigin = "center center";
    petal.style.transform = "scale(0)";
    petal.style.transition = `transform 1s ease-in-out ${index * 0.2}s`;
  });

  setTimeout(() => {
    flower.style.transform = "scale(1)";
    petals.forEach((petal) => {
      petal.style.transform = "scale(1)";
    });
  }, 1000);
}
</script>